import { gsap, Expo } from 'gsap';

const t1 = gsap.timeline({ paused: true });

t1.to('.nav-container', {
  duration: 1,
  left: 0,
  ease: Expo.easeInOut,
});

t1.staggerFrom(
  '.menu > div',
  0.8,
  {
    y: 100,
    opacity: 0,
    ease: Expo.easeOut,
    stagger: 0.1,
  },
  '-=0.4'
);

t1.staggerFrom(
  '.socials',
  0.8,
  {
    y: 100,
    opacity: 0,
    ease: Expo.easeOut,
    stagger: 0.4,
  },
  '-=0.6'
);

t1.reverse();

document.addEventListener('click', (event) => {
  const target = event.target as HTMLElement;

  if (target.classList.contains('menu-open')) {
    t1.reversed(!t1.reversed());
  }
  if (target.classList.contains('menu-close')) {
    t1.reversed(!t1.reversed());
  }
});
