import { gsap } from 'gsap';

export function runCursorAnimation() {
  const cursor = document.querySelector<HTMLElement>('.cursor');
  const follower = document.querySelector<HTMLElement>('.cursor-follower');

  if (!cursor || !follower) {
    throw new Error('Cursor or follower element not found');
  }

  let posX = 0;
  let posY = 0;
  let mouseX = 0;
  let mouseY = 0;

  gsap.to({}, 0.016, {
    repeat: -1,
    onRepeat: () => {
      posX += (mouseX - posX) / 9;
      posY += (mouseY - posY) / 9;

      gsap.set(follower, {
        left: posX - 20,
        top: posY - 20,
      });

      gsap.set(cursor, {
        left: mouseX,
        top: mouseY,
      });
    },
  });

  document.addEventListener('mousemove', (e) => {
    mouseX = e.pageX;
    mouseY = e.pageY;
  });

  const projectElements = document.querySelectorAll<HTMLElement>('.row [class^="project"]');
  projectElements.forEach((element) => {
    element.addEventListener('mouseenter', () => {
      cursor.classList.add('active');
      follower.classList.add('active');
    });

    element.addEventListener('mouseleave', () => {
      cursor.classList.remove('active');
      follower.classList.remove('active');
    });
  });
}
