import { gsap } from 'gsap';

export function runCursorAnimation() {
  const $  = jQuery;
  const cursor = $('.cursor');
  const follower = $('.cursor-follower');
  const cursors = $(".cursors");
  const followers = $(".cursor-followers");

  let posX = 0;
  let posY = 0;
  let mouseX = 0;
  let mouseY = 0;

  gsap.to({}, 0.016, {
    repeat: -1,
    onRepeat: () => {
      posX += (mouseX - posX) / 9;
      posY += (mouseY - posY) / 9;

      gsap.set(follower, {
        css: {
          left: posX - 20,
          top: posY - 20,
        },
      });

      gsap.set(cursor, {
        css: {
          left: mouseX,
          top: mouseY,
        },
      });

      gsap.set(followers, {
        left: posX - 20,
        top: posY - 20
      });

      gsap.set(cursors, {
        left: mouseX,
        top: mouseY
      });
    },
    duration: 0.016
  });

  $(document).on('mousemove', (e) => {
    mouseX = e.pageX;
    mouseY = e.pageY;
  });

  const addCursorListeners = (project: string) => {
    const projectElement = $(`.row .${project}`);
    const awardBodyCerts = $(".award-body .cert");

    projectElement.on('mouseenter', () => {
      cursor.addClass('active');
      follower.addClass('active');
    });

    projectElement.on('mouseleave', () => {
      cursor.removeClass('active');
      follower.removeClass('active');
    });

    awardBodyCerts.on("mouseenter", () => {
      cursors.addClass("active");
      followers.addClass("active");
    });

    awardBodyCerts.on("mouseleave", () => {
      cursors.removeClass("active");
      followers.removeClass("active");
    }); 
  };

  addCursorListeners('project1');
  addCursorListeners('project2');
  addCursorListeners('project3');
  addCursorListeners('project4');
  addCursorListeners('project5');
  addCursorListeners('project6');
  addCursorListeners('project7');
  addCursorListeners('project8');
  addCursorListeners('project9');
  addCursorListeners('project10');
  addCursorListeners('project11');
  addCursorListeners('project12');
  addCursorListeners('project13');

  const image = document.querySelector('.image_div') as HTMLElement;
  image.addEventListener('wheel', (event) => {
    event.preventDefault();
    image.scrollLeft += event.deltaY;
  });
}
