import { gsap, Expo } from 'gsap';
import $ from 'jquery';

export function runMenuAnimation() {
  const t1 = gsap.timeline({ paused: true });

  t1.to('.nav-container', {
    duration: 1,
    left: 0,
    ease: Expo.easeInOut,
  });

  t1.staggerFrom(
    '.menu > div',
    0.8,
    {
      y: 100,
      opacity: 0,
      ease: Expo.easeOut,
      stagger: 0.1,
      position: '-=0.4',
    }
  );

  t1.staggerFrom(
    '.socials',
    0.8,
    {
      y: 100,
      opacity: 0,
      ease: Expo.easeOut,
      stagger: 0.4,
      position: '-=0.6',
    }
  );

  t1.reverse();

  $(document).on('click', '.menu-open', function () {
    t1.reversed(!t1.reversed());
  });

  $(document).on('click', '.menu-close', function () {
    t1.reversed(!t1.reversed());
  });
}
