import { gsap, Expo } from 'gsap';

export async function runMenuAnimation() {
  const t1 = gsap.timeline({ paused: true });

  t1.to('.nav-container', {
    duration: 1,
    left: 0,
    ease: Expo.easeInOut,
  });

  t1.staggerFrom(
    '.menu > div',
    0.8,
    {
      y: 100,
      opacity: 0,
      ease: Expo.easeOut,
      stagger: 0.1,
    },
    '-=0.4'
  );

  t1.staggerTo(
    '.menu > div',
    0.8,
    {
      y: 0,
      opacity: 1,
      ease: Expo.easeOut,
      stagger: 0.1,
    },
    '-=0.4'
  );

  t1.staggerFrom(
    '.socials',
    0.8,
    {
      y: 100,
      opacity: 0,
      ease: Expo.easeOut,
      stagger: 0.4,
    },
    '-=0.6'
  );

  t1.staggerTo(
    '.socials',
    0.8,
    {
      y: 0,
      opacity: 1,
      ease: Expo.easeOut,
      stagger: 0.4,
    },
    '-=0.6'
  );

  t1.reverse();

  document.addEventListener('click', (event) => {
    const target = event.target as HTMLElement;

    if (target.classList.contains('menu-open') || target.classList.contains('menu-close')) {
      t1.reversed(!t1.reversed());
    }
  });

  // Dynamically import WOW.js
  const WOWModule = await import('wowjs');
  const wow = new WOWModule.default();
  wow.init();
}
