import { useHistory } from 'react-router-dom';


const nameInput = document.querySelector<HTMLInputElement>("#name");
const emailInput = document.querySelector<HTMLInputElement>("#email");
const messageInput = document.querySelector<HTMLInputElement>("#message");
const success = document.querySelector("#success") as HTMLElement;
const error = document.querySelectorAll(".error");

export function validateForm(): void {
  clearMessage();
  let errorMessage = false;

  if (nameInput !== null) {
    if (nameInput.value.length < 1) {
        error[0].textContent = "\u0021 Name cannot be blank";
        nameInput.classList.add("error");
        errorMessage = true;
    }
}
if (emailInput !== null && !validEmail(emailInput.value)) {
  const emailError = error[1] as HTMLElement; // Cast error[1] to HTMLElement
  emailError.textContent = "\u0021 Invalid Email Address";
  emailInput.classList.add("error");
  errorMessage = true;
}

if (messageInput !== null && messageInput.value.length < 1) {
  const messageError = error[2] as HTMLElement; // Cast error[2] to HTMLElement
  messageError.textContent = "\u0021 Please Enter a Message";
  messageInput.classList.add("error");
  errorMessage = true;
}

  if (!errorMessage) {
    success.innerText = "Message Sent";
    history.goBack();
  }
}

function clearMessage(): void {
  for (let i = 0; i < error.length; i++) {
    const errorElement = error[i] as HTMLElement;
    errorElement.innerText = "";
}

if (success !== null) {
    success.innerText = "";
}

if (nameInput !== null) {
    nameInput.classList.remove("error");
}

if (emailInput !== null) {
    emailInput.classList.remove("error");
}

if (messageInput !== null) {
    messageInput.classList.remove("error");
}
}

function validEmail(emailInput: string): boolean {
  let symbol = /\S+@\S+\.\S+/;
  return symbol.test(emailInput);
}

// Attach event listener for form validation
const form = document.querySelector("form");
if (form) {
  form.addEventListener("submit", (event) => {
    event.preventDefault();
    validateForm();
  });
}
